import { useEffect, useState } from 'react'
import { Link, useSubmit } from 'react-router-dom'
import PetsIcon from '@mui/icons-material/Pets'
import classes from './CatItem.module.css'

function CatItem({ cat }) {
	const submit = useSubmit()
	const [likes, setLikes] = useState(0)

    async function updateLikes() {
		const requestOptions = {
			method: 'PUT',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify({ likes: likes + 1 })
		};
		const response = await fetch(`http://localhost:8080/cats/${cat.id}`, requestOptions);
		const data = await response.json();
		setLikes(data.likes);
    }

	function startDeleteHandler() {
		const proceed = window.confirm('Are you sure?')

		if (proceed) {
		  submit(null, { method: 'delete' })
		}
	}

	function pawHandler() {
		updateLikes()
	}

	console.log(cat)

	return (
		<article className={classes.cat}>
			<img src={cat.image} alt={cat.title} />
			<h1>{cat.title}</h1>
			<time>{cat.date}</time>
			<p>{cat.description}</p>
			<p>{cat.likes}</p>
			<menu className={classes.actions}>
				<Link to="edit">Edit</Link>
				<button onClick={startDeleteHandler}>Delete</button>
				<button onClick={pawHandler}>{cat.likes}</button>
			</menu>
		</article>
	)
}

export default CatItem

