import { json, redirect, useRouteLoaderData } from "react-router-dom"
import CatItem from '../components/CatItem'

function CatDetailPage({ method }) {

	// Get identifier from the path 
	//const params = useParams()
	//const data = useLoaderData()

	// useRouteLoaderData takes route id as an arg
	const data = useRouteLoaderData('cat-detail')
	const cat = data.cat

	return (
		<CatItem method={method} cat={cat} />
	)
}

export default CatDetailPage

export async function loader({ request, params }) {
	const id = params.catId

	const response = await fetch('http://localhost:8080/cats/' + id)

	if (!response.ok) {
		throw json(
			{ message: 'Could not fetch details for the selected cat'},
			{ status: 500 }
			)
	} else {
		return response
	}
}

export async function likeCatAction({ request, params }) {
	const method = request.method
	const data = await request.formData()

	const likesData = {
		likes: data.get('likes')
	}

	const catId = params.catId
	let url = 'http://localhost:8080/cats/' + catId

	const response = await fetch(url, {
		method: method,
		headers: {
			'Content-Type': 'application/json',
		},
		body: JSON.stringify(likesData),
	})

	// Status code from the backend
	if (response.status === 422) {
		return response
	}

	if (!response.ok) {
		throw json({ message: 'Could not give paws up' }, { status: 500 })
	}
}

export async function action({ params }) {
	const catId = params.catId
	const response = await fetch('http://localhost:8080/cats/' + catId, {
		method: 'DELETE'
	})

	if (!response.ok) {
		throw json(
			{ message: 'Could not delete cat'},
			{ status: 500 }
			)
	} 
	return redirect('/cats')
}
