import { useEffect, useState } from 'react'
import { Link, useSubmit, useMatch } from 'react-router-dom'
import PetsIcon from '@mui/icons-material/Pets'
import classes from './CatItem.module.css'

function CatItem({ cat }) {
	const submit = useSubmit()
	const [likes, setLikes] = useState(null)

	const match = useMatch('/cats/:catId')
	let catId = match.params.catId

	// useEffect(() => {
	// 	async function getLikes() {
	// 		let response = await fetch(`http://localhost:8080/cats/${catId}`);
	// 		let data = await response.json();
	// 		console.log(data.cat.likes)
	// 		setLikes(data.cat.likes);
	// 	};
	// 	getLikes()
	// }, [catId, likes]);

	// async function updateLikes() {
	// 	await fetch(`http://localhost:8080/cats/${catId}`, {
	// 	 method: "PUT",
	// 	 headers: {
	// 	  "Content-Type": "application/json",
	// 	 },
	// 	 body: JSON.stringify({ ...cat, likes: likes }),
	// 	});
	// };

	useEffect(() => {
        // PUT request using fetch with set headers
        const requestOptions = {
            method: 'PUT',
            headers: { 
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ ...cat, likes: likes })
        };
        fetch(`http://localhost:8080/cats/${catId}`, requestOptions)
            .then(response => response.json())
            .then(data => setLikes(data.cat.likes));
    }, []);

	function pawHandler() {
		console.log(likes)
		setLikes(l => l + 1)
		console.log(likes)
		updateLikes();
		console.log(likes)
	};

	function startDeleteHandler() {
		const proceed = window.confirm('Are you sure?')

		if (proceed) {
		  submit(null, { method: 'delete' })
		}
	}

	// function pawHandler() {
	// 	setLikes(v => v + 1)
	// 	updateLikes({
	// 		likes: likes
	// 	})
	// }

	console.log(cat)

	return (
		<article className={classes.cat}>
			<img src={cat.image} alt={cat.title} />
			<h1>{cat.title}</h1>
			<time>{cat.date}</time>
			<p>{cat.description}</p>
			<p>{cat.likes}</p>
			<menu className={classes.actions}>
				<Link to="edit">Edit</Link>
				<button onClick={startDeleteHandler}>Delete</button>
				<button onClick={pawHandler}>{likes}</button>
			</menu>
		</article>
	)
}

export default CatItem

