import { useEffect, useState } from 'react'
import { Link, useSubmit, useMatch } from 'react-router-dom'
import PetsIcon from '@mui/icons-material/Pets'
import classes from './CatItem.module.css'

function CatItem({ cat }) {
	const submit = useSubmit()
	const [likes, setLikes] = useState(0)

	const match = useMatch('/cats/:catId')
	let catId = match.params

	console.log(catId)

	useEffect(() => {
		getLikes();
	   }, [catId]);

	let getLikes = async () => {
		let response = await fetch(`http://localhost:3000/cats/${catId}`);
		let data = await response.json();
		setLikes(data);
	   };

	let updateLikes = async () => {
		await fetch(`http://localhost:3000/cats/${catId}`, {
		 method: "PUT",
		 headers: {
		  "Content-Type": "application/json",
		 },
		 body: JSON.stringify({ ...cat.likes, updated: likes }),
		});
	   };

	let pawHandler = async () => {
		await updateLikes();
	   };

	function startDeleteHandler() {
		const proceed = window.confirm('Are you sure?')

		if (proceed) {
		  submit(null, { method: 'delete' })
		}
	}

	// function pawHandler() {
	// 	setLikes(v => v + 1)
	// 	updateLikes({
	// 		likes: likes
	// 	})
	// }

	console.log(cat)

	return (
		<article className={classes.cat}>
			<img src={cat.image} alt={cat.title} />
			<h1>{cat.title}</h1>
			<time>{cat.date}</time>
			<p>{cat.description}</p>
			<p>{cat.likes}</p>
			<menu className={classes.actions}>
				<Link to="edit">Edit</Link>
				<button onClick={startDeleteHandler}>Delete</button>
				<button onClick={pawHandler}>{cat.likes}</button>
			</menu>
		</article>
	)
}

export default CatItem

